var m = {
    init: function() {
        var a = this;
        this.carousel(5e3);
        this.bindEvents();
    },
    bindEvents: function() {
        var a = this;
        var b = 0;
        $(".product-list .product-title a").hover(function() {
            $(".product-list .product-title").each(function() {
                $(this).removeClass("active");
            });
            var a = parseInt($(this).data("img"));
            a -= 1;
            $(this).parent().addClass("active");
            $("#products-wrapper .prdbg").each(function() {
                $(this).hide();
            });
            $("#products-wrapper .prdbg:eq(" + a + ")").show();
        });
        $("#btnMenu,.menu-itemsl").click(function() {
            a.toggleMenu();
        });
        $(".ip").focus(function() {
            $(this).siblings().fadeOut(150);
        });
        $(".ip").focusout(function() {
            if ($(this).val().length === 0) {
                $(this).siblings().fadeIn(150);
            }
        });
        $('input[type="reset"]').click(function() {
            $("label").each(function() {
                $(this).fadeIn(150);
            });
        });
        $("label").click(function() {
            $(this).fadeOut(15).siblings().focus();
        });
        $(".nav-link:not(.href)").click(function(b) {
            b.preventDefault();
            $(".nav-link-list").each(function() {
                $(this).removeClass("active");
            });
            var c = $(this).attr("href");
            $(this).parent().addClass("active");
            if (c.length) {
                if (c === "#") {
                    $("html, body").animate({
                        scrollTop: $("body").offset().top
                    }, 500);
                } else {
                    $("html, body").animate({
                        scrollTop: $("" + c).offset().top
                    }, 500);
                }
            }
            a.toggleMenu();
        });
    },
    toggleMenu: function() {
        $("body,nav").toggleClass("menu-open");
    },
    carousel: function(a) {
        var b = this, c, d, e = 0, f = 0, g = a, h = 0, i = 1, j = parseInt($(".slide").length) - 1;
        b.starCarousel = function() {
            $(".slide.active").removeClass("in").addClass("out");
            $(".slide .progress").css("width", "0");
            setTimeout(function() {
                b.changeSlide();
            }, 600);
        };
        b.changeSlide = function() {
            $(".slide,.indicators li").each(function() {
                $(this).removeClass("active out");
            });
            $(".slide:eq(" + i + "),.indicators li:eq(" + i + ")").addClass("active");
            setTimeout(function() {
                $(".slide:eq(" + i + "),.indicators li:eq(" + i + ")").addClass("in");
                $(".slide:eq(" + i + ") .progress").animate({
                    width: "100%"
                }, 4400);
                if (i < j) {
                    i++;
                } else {
                    i = 0;
                }
            }, 100);
        };
        b.pause = function() {
            if (f != 1) return;
            e = g - (new Date() - d);
            window.clearInterval(c);
            f = 2;
        };
        b.resume = function() {
            if (f != 2) return;
            f = 3;
            window.setTimeout(b.timeoutCallback, e);
        };
        b.timeoutCallback = function() {
            if (f != 3) return;
            b.starCarousel();
            d = new Date();
            c = window.setInterval(b.starCarousel, g);
            f = 1;
        };
        b.init = function() {
            d = new Date();
            $(".slide:eq(" + (i - 1) + ") .progress").animate({
                width: "100%"
            }, g - 600);
            c = window.setInterval(b.starCarousel, g);
            f = 1;
        };
        this.init();
    }
};

m.init();